import groovy.json.JsonSlurper

def checkExecution(responseContent, executionKeySet){
	jsonKeySet = Eval.me(executionKeySet).sort()
	keySetResponse = responseContent.keySet().sort() 

	log.info("comparing "+keySetResponse+" with "+jsonKeySet)
	return (keySetResponse == jsonKeySet)
}

log.info("verifying initExecution step")
responseContent = new JsonSlurper().parseText(testRunner.testCase.getTestStepByName("initExecution").getPropertyValue("response") )
assert  checkExecution(responseContent, context.expand('${#Project#executionKeyValues}')) == true

log.info("verifying listExecution step")
reponseFullList =  new JsonSlurper().parseText(testRunner.testCase.getTestStepByName("listExecutions").getPropertyValue("response"))
reponseFullList.each { assert  checkExecution(it, context.expand('${#Project#executionKeyValues}')) == true }

